package com.moli.module.framework.utils.rximport android.view.Viewimport com.jakewharton.rxbinding2.internal.VoidToUnitimport com.jakewharton.rxbinding2.view.RxViewimport io.reactivex.Observableimport java.util.concurrent.TimeUnit/** * 项目名称：Platformer * 类描述： * 创建人：liujun * 创建时间：2017/9/22 18:19 * 修改人：liujun * 修改时间：2017/9/22 18:19 * 修改备注： * @version */fun View.clicksThrottle(): Observable<Unit> = this.clicksThrottle(1000)fun View.clicksThrottle(windowDuration: Long): Observable<Unit> = RxView.clicks(this).throttleFirst(windowDuration, TimeUnit.MILLISECONDS).map(VoidToUnit)