package com.moli.module.model.baseimport android.os.Parcelimport android.os.Parcelableimport com.google.gson.annotations.SerializedNamedata class VersionModel(    val clientName: String? = null, //guanwang    @JvmField    @SerializedName(value = "operateStat")    var isImpose: Int = 0,//是否强制更新,0正常 1需要跟新 2表示强制更新    var serverwebUrl: String? = null,    //域名地址    val clientVersion: String? = null, ////版本1.0.0    @JvmField    @SerializedName(value = "clientType")  //包名com.moli.pophelper    val packageName: String? = null,    val updateUrl: String? = null,  //下载url    val updateVersion: String? = null,    val updateContent: String? = null,    val isDebug: Int? = 0,    val isMaintain: Int = 0,//维护状态 0正常 1维护    val createTime: Long? = 0//时间   //创建时间) : Parcelable {    constructor(source: Parcel) : this(        source.readString(),        source.readInt(),        source.readString(),        source.readString(),        source.readString(),        source.readString(),        source.readString(),        source.readString(),        source.readValue(Int::class.java.classLoader) as Int?,        source.readInt(),        source.readValue(Long::class.java.classLoader) as Long?    )    override fun describeContents() = 0    override fun writeToParcel(dest: Parcel, flags: Int) = with(dest) {        writeString(clientName)        writeInt(isImpose)        writeString(serverwebUrl)        writeString(clientVersion)        writeString(packageName)        writeString(updateUrl)        writeString(updateVersion)        writeString(updateContent)        writeValue(isDebug)        writeInt(isMaintain)        writeValue(createTime)    }    companion object {        @JvmField        val CREATOR: Parcelable.Creator<VersionModel> = object : Parcelable.Creator<VersionModel> {            override fun createFromParcel(source: Parcel): VersionModel = VersionModel(source)            override fun newArray(size: Int): Array<VersionModel?> = arrayOfNulls(size)        }    }}